-- Name: lk_ap; Type: VIEW; Schema: alkis; Owner: -
--

CREATE VIEW lk_ap AS
 SELECT o.oid,
    o.gml_id,
    o.beginnt,
    o.endet,
    o.punktkennung,
    o.sonstigeeigenschaft,
    o.vermarkung_marke,
    l.beschreibung AS verm_bedeutung,
    p.genauigkeitsstufe,
    m.beschreibung AS punktgenauigkeit,
    p.vertrauenswuerdigkeit,
    n.beschreibung AS bedeutung,
    rtrim(ltrim(public.st_astext(p.wkb_geometry), 'POINT('::text), ')'::text) AS koord,
    p.wkb_geometry
   FROM ((((ax_aufnahmepunkt o
     LEFT JOIN ax_punktortau p ON ((o.gml_id = ANY (p.istteilvon))))
     LEFT JOIN ax_marke l ON ((l.wert = o.vermarkung_marke)))
     LEFT JOIN ax_genauigkeitsstufe_punktort m ON ((m.wert = p.genauigkeitsstufe)))
     LEFT JOIN ax_vertrauenswuerdigkeit_punktort n ON ((n.wert = p.vertrauenswuerdigkeit)));


