--
-- Name: lk_gebaeude; Type: VIEW; Schema: alkis; Owner: -
--

CREATE VIEW lk_gebaeude AS
 SELECT o.oid,
    o.ogc_fid,
    o.gml_id,
    o.beginnt,
    o.endet,
    o.gebaeudefunktion,
    p.beschreibung AS bezeichner,
    w.wert AS weiterefunktion,
    o.name,
    o.zustand,
    z.beschreibung AS gebaeudezustand,
    o.objekthoehe,
    o.lagezurerdoberflaeche,
    o.dachform,
    d.beschreibung AS dach_bezeichner,
    o.hochhaus,
    o.herkunft_source_source_ax_datenerhebung[1] AS ax_datenerhebung,
    da.beschreibung AS herkunft,
    o.wkb_geometry
   FROM (((((ax_gebaeude o
     LEFT JOIN ax_gebaeudefunktion p ON ((p.wert = o.gebaeudefunktion)))
     LEFT JOIN ax_dachform d ON ((d.wert = o.dachform)))
     LEFT JOIN ax_zustand_gebaeude z ON ((z.wert = o.zustand)))
     LEFT JOIN ax_weitere_gebaeudefunktion w ON ((w.wert = ANY (o.weiteregebaeudefunktion))))
     LEFT JOIN ax_datenerhebung da ON ((da.wert = (o.herkunft_source_source_ax_datenerhebung[1])::integer)));


--
-- Name: lk_gebaeudepunkte; Type: VIEW; Schema: alkis; Owner: -
