    m.beschreibung AS punktgenauigkeit,
    p.vertrauenswuerdigkeit,
    n.beschreibung,
    p.wkb_geometry,
    rtrim(ltrim(public.st_astext(p.wkb_geometry), 'POINT('::text), ')'::text) AS koord
   FROM (((ax_besonderergebaeudepunkt o
     LEFT JOIN ax_punktortag p ON ((o.gml_id = ANY (p.istteilvon))))
     LEFT JOIN ax_genauigkeitsstufe_punktort m ON ((m.wert = p.genauigkeitsstufe)))
     LEFT JOIN ax_vertrauenswuerdigkeit_punktort n ON ((n.wert = p.vertrauenswuerdigkeit)));


