    e.beschreibung AS datenerhebung,
    p.genauigkeitsstufe,
    m.beschreibung AS punktgenauigkeit,
    p.vertrauenswuerdigkeit,
    n.beschreibung AS punktvertrauenswuerdigkeit,
    p.wkb_geometry
   FROM (((((((ax_grenzpunkt o
     LEFT JOIN ax_punktortta p ON (((o.gml_id)::text = ANY ((p.istteilvon)::text[]))))
     LEFT JOIN ax_marke l ON ((l.wert = o.abmarkung_marke)))
     LEFT JOIN ax_genauigkeitsstufe_punktort m ON ((m.wert = p.genauigkeitsstufe)))
     LEFT JOIN ax_vertrauenswuerdigkeit_punktort n ON ((n.wert = p.vertrauenswuerdigkeit)))
     LEFT JOIN ax_bemerkungzurabmarkung_grenzpunkt ba ON ((o.bemerkungzurabmarkung = ba.wert)))
     LEFT JOIN ax_datenerhebung e ON ((e.wert = ANY ((p.herkunft_source_source_description)::integer[]))))
     LEFT JOIN ax_dienststelle d ON (((o.zustaendigestelle_stelle)::text = (d.stelle)::text)))
