--
-- Name: s_hausnummer_gebaeude; Type: VIEW; Schema: alkis; Owner: -
--

CREATE VIEW s_hausnummer_gebaeude AS
 SELECT p.ogc_fid,
    p.beginnt,
    p.endet,
    l.hausnummer,
    (p.drehwinkel * (57.296)::double precision) AS drehwinkel,
    p.wkb_geometry
   FROM (ap_pto p
     JOIN ax_lagebezeichnungmithausnummer l ON ((((l.gml_id)::text = ANY ((p.dientzurdarstellungvon)::text[])) AND (l.endet IS NULL))))
  WHERE ((p.art)::text = 'HNR'::text);


--
-- Name: s_zugehoerigkeitshaken_flurstueck; Type: VIEW; Schema: alkis; Owner: -
--

CREATE VIEW s_zugehoerigkeitshaken_flurstueck AS
 SELECT p.ogc_fid,
    p.beginnt,
    p.endet,
    p.wkb_geometry,
    (p.drehwinkel * (57.296)::double precision) AS drehwinkel,
    f.flurstueckskennzeichen,
    f.abweichenderrechtszustand
   FROM (ap_ppo p
     JOIN ax_flurstueck f ON (((f.gml_id)::text = ANY ((p.dientzurdarstellungvon)::text[]))))
  WHERE ((p.art)::text = 'Haken'::text);


--
-- Name: s_zuordungspfeil_flurstueck; Type: VIEW; Schema: alkis; Owner: -
--

CREATE VIEW s_zuordungspfeil_flurstueck AS
 SELECT l.ogc_fid,
    l.beginnt,
    l.endet,
    f.abweichenderrechtszustand,
    l.wkb_geometry
   FROM (ap_lpo l
     JOIN ax_flurstueck f ON ((f.gml_id = ANY (l.dientzurdarstellungvon))))
  WHERE (((l.art)::text = 'Pfeil'::text) AND ('DKKM1000'::text ~~ ANY ((l.advstandardmodell)::text[])));


