INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (1000, 'Erden, Lockergestein', 'Erden, Lockergestein bedeutet, dass feinkörnige Gesteine abgebaut werden.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (1001, 'Ton', 'Ton ist ein Abbaugut, das aus gelblichem bis grauem Lockergestein besteht und durch Verwitterung älterer Gesteine entsteht.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (1007, 'Kalk, Kalktuff, Kreide', 'Kalk, Kalktuff, Kreide ist ein Abbaugut, das aus erdigem weißen Kalkstein besteht.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (2000, 'Steine, Gestein, Festgestein', 'Steine, Gestein, Festgestein bedeutet, dass grobkörnige oder feste Gesteine abgebaut werden.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (2002, 'Schiefer, Dachschiefer', 'Schiefer, Dachschiefer ist ein toniges Abbaugut, das in dünne ebene Platten spaltbar ist.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (2003, 'Metamorpher Schiefer', 'Metamorpher Schiefer ist ein Abbaugut, dessen ursprüngliche Zusammensetzung und Struktur durch Wärme und Druck innerhalb der Erdkruste verändert worden ist.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (2005, 'Kalkstein', 'Kalkstein ist ein Abbaugut, das als weit verbreitetes Sedimentgestein überwiegend aus Calciumcarbonat besteht.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (2006, 'Dolomitstein', 'Dolomitstein ist ein Abbaugut, das überwiegend aus calcium- und magnesiumhaltigen Mineralien besteht.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (2013, 'Basalt, Diabas', 'Basalt, Diabas ist ein Abbaugut, das aus basischem Ergussgestein besteht.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (2021, 'Talkschiefer, Speckstein', 'Talkschiefer, Speckstein ist ein farbloses bis graugrünes, sich fettig anfühlendes Abbaugut, das aus dem weichen Mineral Talk besteht.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (3000, 'Erze', 'Erze bedeutet, dass die in der Natur vorkommenden, metallhaltigen Mineralien und Mineralgemische abgebaut oder gespeichert werden.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (3001, 'Eisen', 'Eisen wird als Eisenerz abgebaut und durch Verhüttung gewonnen.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (3002, 'Buntmetallerze', 'Buntmetallerze ist das Abbaugut, das alle Nichteisenmetallerze als Sammelbegriff umfasst.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (3003, 'Kupfer', 'Kupfer wird als Kupfererz abgebaut und durch Verhüttung gewonnen.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (3004, 'Blei', 'Blei wird als Bleierz abgebaut und durch spezielle Verfahren gewonnen.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (3005, 'Zink', 'Zink wird als Zinkerz abgebaut und durch spezielle Verfahren gewonnen.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (3006, 'Zinn', 'Zinn wird als Zinnerz abgebaut und durch spezielle Verfahren gewonnen.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (3007, 'Wismut, Kobalt, Nickel', 'Wismut, Kobalt, Nickel werden als Erze abgebaut und durch spezielle Verfahren gewonnen.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (3008, 'Uran', 'Uran wird als Uranerz abgebaut und durch spezielle Verfahren gewonnen.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (3009, 'Mangan', 'Mangan wird als Manganerz abgebaut und durch spezielle Verfahren gewonnen.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (3010, 'Antimon', 'Antimon wird als Antimonerz abgebaut und durch spezielle Verfahren gewonnen.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (3011, 'Edelmetallerze', 'Edelmetallerze ist das Abbaugut, aus dem Edelmetalle (z. B. Gold, Silber) gewonnen werden.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (4000, 'Treib- und Brennstoffe', 'Treib- und Brennstoffe bedeutet, dass die in der Natur vorkommenden brennbaren organischen und anorganischen Substanzen abgebaut oder gewonnen werden.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (4020, 'Kohle', 'Kohle ist ein Abbaugut, das durch Inkohlung (Umwandlungsprozess pflanzlicher Substanzen) entstanden ist.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (4021, 'Braunkohle', 'Braunkohle ist ein Abbaugut, das durch einen bestimmten Grad von Inkohlung (Umwandlungsprozess pflanzlicher Substanzen) entstanden ist.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (4022, 'Steinkohle', 'Steinkohle ist ein Abbaugut, das durch vollständige Inkohlung (Umwandlungsprozess pflanzlicher Substanzen) entstanden ist.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (4030, 'Ölschiefer', 'Ölschiefer ist ein Abbaugut, das aus dunklem, bitumenhaltigem, tonigem Gestein besteht.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (5000, 'Industrieminerale, Salze', 'Industrieminerale, Salze bedeutet, dass die in der Natur vorkommenden Mineralien abgebaut werden.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (5001, 'Gipsstein', 'Gipsstein ist ein natürliches Abbaugut.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (5002, 'Anhydritstein', 'Anhydritstein ist ein Abbaugut, das aus wasserfreiem Gips besteht.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (5003, 'Steinsalz', 'Steinsalz ist ein Abbaugut, das aus Salzstöcken gewonnen wird und aus Natriumchlorid besteht.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (5004, 'Kalisalz', 'Kalisalz ist ein Abbaugut, das aus Salzstöcken gewonnen wird und aus Chloriden und Sulfaten besteht.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (5005, 'Kalkspat', 'Kalkspat ist ein weißes oder hell gefärbtes Abbaugut (Calciumcarbonat).');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (5006, 'Flussspat', 'Flussspat ist ein Abbaugut, das aus Calciumfluorid besteht.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (5007, 'Schwerspat', 'Schwerspat ist ein formenreiches, rhombisches weißes bis farbiges Abbaugut.');
INSERT INTO ax_abbaugut_bergbaubetrieb (wert, beschreibung, dokumentation) VALUES (5011, 'Graphit', 'Graphit ist ein bleigraues, weiches, metallglänzendes Abbaugut, das aus fast reinem Kohlenstoff besteht.');


