INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (1000, 'Erden, Lockergestein', 'Erden, Lockergestein bedeutet, dass feinkörnige Gesteine abgebaut werden.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (1001, 'Ton', 'Ton ist ein Abbaugut, das aus gelblichem bis grauem Lockergestein besteht und durch Verwitterung älterer Gesteine entsteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (1002, 'Bentonit', 'Bentonit ist ein tonartiges Abbaugut, das durch Verwitterung vulkanischer Asche (Tuffe) entstanden ist.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (1003, 'Kaolin', 'Kaolin ist ein Abbaugut, das aus weißem, erdigem Gestein, fast reinem Aluminiumsilikat (kieselsaure Tonerde) besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (1004, 'Lehm', 'Lehm ist ein Abbaugut, das durch Verwitterung entstanden ist und aus gelb bis braun gefärbtem sandhaltigem Ton besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (1005, 'Löß, Lößlehm', 'Löß, Lößlehm ist ein Abbaugut das aus feinsten gelblichen Sedimenten besteht und eine hohe Wasserspeicherfähigkeit aufweist.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (1006, 'Mergel', 'Mergel ist ein Abbaugut das aus kalk- und tonartigem Sedimentgestein besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (1007, 'Kalk, Kalktuff, Kreide', 'Kalk, Kalktuff, Kreide ist ein Abbaugut, das aus erdigem weißen Kalkstein besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (1008, 'Sand', 'Sand ist ein Abbaugut, das aus kleinen, losen Mineralkörnern (häufig Quarz) besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (1009, 'Kies, Kiessand', 'Kies, Kiessand ist ein Abbaugut, das aus vom Wasser rund geschliffenen Gesteinsbrocken besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (1011, 'Farberden', 'Farberden ist ein Abbaugut, das durch Verwitterung entstanden ist und vorrangig aus eisenhaltigem Gestein besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (1012, 'Quarzsand', 'Quarzsand ist ein Abbaugut, das vorwiegend aus kleinen, losen Quarzkörnern besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (1013, 'Kieselerde', 'Kieselerde ist ein Abbaugut, das durch tertiäre Binnenseeablagerungen aus Kieselschalen toter Kieselalgen entstanden ist.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2000, 'Steine, Gestein, Festgestein', 'Steine, Gestein, Festgestein bedeutet, dass grobkörnige oder feste Gesteine abgebaut werden.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2001, 'Tonstein', 'Tonstein ist ein gelblich bis graues Abbaugut, das überwiegend aus Tonmineralien besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2002, 'Schiefer, Dachschiefer', 'Schiefer, Dachschiefer ist ein toniges Abbaugut, das in dünne ebene Platten spaltbar ist.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2003, 'Metamorpher Schiefer', 'Metamorpher Schiefer ist ein Abbaugut, dessen ursprüngliche Zusammensetzung und Struktur durch Wärme und Druck innerhalb der Erdkruste verändert worden ist.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2004, 'Mergelstein', 'Mergelstein ist ein Abbaugut, das sich größtenteils aus Ton und Kalk zusammensetzt.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2005, 'Kalkstein', 'Kalkstein ist ein Abbaugut, das als weit verbreitetes Sedimentgestein überwiegend aus Calciumcarbonat besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2006, 'Dolomitstein', 'Dolomitstein ist ein Abbaugut, das überwiegend aus calcium- und magnesiumhaltigen Mineralien besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2007, 'Travertin', 'Travertin ist ein Abbaugut, das aus gelblichen Kiesel- oder Kalktuffen besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2008, 'Marmor', 'Marmor ist ein Abbaugut, das als rein weißer kristalliner, körniger Kalkstein (Calciumcarbonat) vorkommt.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2009, 'Sandstein', 'Sandstein ist ein Abbaugut, das aus verfestigtem Sedimentgestein besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2010, 'Grauwacke', 'Grauwacke ist ein Abbaugut, das aus tonhaltigem Sandstein besteht und mit Gesteinsbruchstücken angereichert sein kann.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2011, 'Quarzit', 'Quarzit ist ein sehr hartes metamorphes Abbaugut, das vorwiegend aus feinkörnigen Quarzmineralien besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2012, 'Gneis', 'Gneis ist ein metamorphes Abbaugut mit Schieferung, das aus Feldspat, Quarz und Glimmer besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2013, 'Basalt, Diabas', 'Basalt, Diabas ist ein Abbaugut, das aus basischem Ergussgestein besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2014, 'Andesit', 'Andesit ist ein Abbaugut, das aus Ergussgestein besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2015, 'Porphyr, Quarzporphyr', 'Porphyr, Quarzporphyr ist ein eruptiv entstandenes Abbaugut, das aus einer dichten Grundmasse und groben Einsprenglingen besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2016, 'Granit', 'Granit ist ein eruptiv entstandenes Abbaugut, das aus körnigem Feldspat, Quarz, Glimmer besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2017, 'Granodiorit', 'Granodiorit ist ein hell- bis dunkelgraues Abbaugut. Es ist ein mittelkörniges Tiefengestein mit den Hauptbestandteilen Feldspat, Quarz, Hornblende und Biotit.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2018, 'Tuff-, Bimsstein', 'Tuff-, Bimsstein ist ein helles, sehr poröses Abbaugut, das durch rasches Erstarren der Lava entstanden ist.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2019, 'Trass', 'Trass ist ein Abbaugut, das aus vulkanischem Aschentuff (Bimsstein) besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2020, 'Lavaschlacke', 'Lavaschlacke ist ein Abbaugut, das aus ausgestoßenem, geschmolzenen Vulkangestein besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (2021, 'Talkschiefer, Speckstein', 'Talkschiefer, Speckstein ist ein farbloses bis graugrünes, sich fettig anfühlendes Abbaugut, das aus dem weichen Mineral Talk besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (4000, 'Treib- und Brennstoffe', 'Treib- und Brennstoffe bedeutet, dass die in der Natur vorkommenden brennbaren organischen und anorganischen Substanzen abgebaut oder gewonnen werden.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (4010, 'Torf', 'Torf ist ein Abbaugut, das aus der unvollkommenen Zersetzung abgestorbener pflanzlicher Substanz unter Luftabschluss in Mooren entstanden ist.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (4020, 'Kohle', 'Kohle ist ein Abbaugut, das durch Inkohlung (Umwandlungsprozess pflanzlicher Substanzen) entstanden ist.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (4021, 'Braunkohle', 'Braunkohle ist ein Abbaugut, das durch einen bestimmten Grad von Inkohlung (Umwandlungsprozess pflanzlicher Substanzen) entstanden ist.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (4022, 'Steinkohle', 'Steinkohle ist ein Abbaugut, das durch vollständige Inkohlung (Umwandlungsprozess pflanzlicher Substanzen) entstanden ist.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (4030, 'Ölschiefer', 'Ölschiefer ist ein Abbaugut, das aus dunklem, bitumenhaltigen, tonigen Gestein besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (5000, 'Industrieminerale, Salze', 'Industrieminerale, Salze bedeutet, dass die in der Natur vorkommenden Mineralien abgebaut werden.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (5001, 'Gipsstein', 'Gipsstein ist ein natürliches Abbaugut.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (5002, 'Anhydritstein', 'Anhydritstein ist ein Abbaugut, das aus wasserfreiem Gips besteht.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (5005, 'Kalkspat', 'Kalkspat ist ein weißes oder hell gefärbtes Abbaugut (Calciumcarbonat).');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (5007, 'Schwerspat', 'Schwerspat ist ein formenreiches, rhombisches weißes bis farbiges Abbaugut.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (5008, 'Quarz', 'Quarz ist ein Abbaugut, das aus verschiedenen Gesteinsarten (Granit, Gneis, Sandstein) gewonnen wird.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (5009, 'Feldspat', 'Feldspat ist ein weiß bis grauweißes gesteinsbildendes Mineral von blättrigem Bruch, das abgebaut wird.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (5010, 'Pegmatitsand', 'Pegmatitsand ist ein Abbaugut, das durch Verwitterung von Granit und Gneis entstanden ist.');
INSERT INTO ax_abbaugut_tagebaugrubesteinbruch (wert, beschreibung, dokumentation) VALUES (9999, 'Sonstiges', 'Sonstiges bedeutet, dass das Abbaugut bekannt, aber nicht in der Attributwertliste aufgeführt ist.');


