INSERT INTO ax_archaeologischertyp_historischesbauwerkoderhistorischee (wert, beschreibung, dokumentation) VALUES (1000, 'Grab', 'Grab ist eine künstlich geschaffene Bestattungsstätte unter, auf oder über der  Erdoberfläche.');
INSERT INTO ax_archaeologischertyp_historischesbauwerkoderhistorischee (wert, beschreibung, dokumentation) VALUES (1010, 'Großsteingrab (Dolmen, Hünenbett)', 'Großsteingrab (Dolmen, Hünenbett) ist ein Grab mit Steineinbau, d. h. es ist ein aus großen Steinen (z.B. Findlingen) errichteter Grabbau.');
INSERT INTO ax_archaeologischertyp_historischesbauwerkoderhistorischee (wert, beschreibung, dokumentation) VALUES (1020, 'Grabhügel (Hügelgrab)', 'Grabhügel (Hügelgrab) ist ein meist runder oder ovaler Hügel, der über einer ur- oder frühgeschichtlichen Bestattung aus Erde aufgeschüttet oder aus Plaggen aufgeschichtet  wurde.');
INSERT INTO ax_archaeologischertyp_historischesbauwerkoderhistorischee (wert, beschreibung, dokumentation) VALUES (1100, 'Historische Wasserleitung', 'Historische Wasserleitung ist ein meist offenes System von Gräben, Kunstgräben und Kanälen, in dem Wasser transportiert wird.');
INSERT INTO ax_archaeologischertyp_historischesbauwerkoderhistorischee (wert, beschreibung, dokumentation) VALUES (1110, 'Aquädukt', 'Aquädukt ist ein brückenartiges Steinbauwerk zur Überführung von Freispiegel-Wasserleitungen mit  natürlichem Gefälle über Täler oder andere Bodenunebenheiten.');
INSERT INTO ax_archaeologischertyp_historischesbauwerkoderhistorischee (wert, beschreibung, dokumentation) VALUES (1200, 'Befestigung (Wall, Graben)', 'Befestigung (Wall, Graben) ist ein aus Erde aufgeschütteter Grenz-, Schutz- oder Stadtwall. Zu der Befestigung (Wall) zählen auch Limes und Landwehr.');
INSERT INTO ax_archaeologischertyp_historischesbauwerkoderhistorischee (wert, beschreibung, dokumentation) VALUES (1210, 'Wachturm (römisch), Warte', 'Wachtturm (römisch), Warte ist ein allein oder in Verbindung mit einem Befestigungssystem (Limes) stehender Beobachtungsturm.');
INSERT INTO ax_archaeologischertyp_historischesbauwerkoderhistorischee (wert, beschreibung, dokumentation) VALUES (1300, 'Steinmal', 'Steinmal ist eine kultische oder rechtliche Kennzeichnung, bestehend aus einzelnen oder Gruppen von Steinen.');
INSERT INTO ax_archaeologischertyp_historischesbauwerkoderhistorischee (wert, beschreibung, dokumentation) VALUES (1400, 'Befestigung (Burgruine)', 'Befestigung (Burgruine) ist eine künstliche Anlage zur Sicherung von Leben und Gut.');
INSERT INTO ax_archaeologischertyp_historischesbauwerkoderhistorischee (wert, beschreibung, dokumentation) VALUES (1410, 'Burg (Fliehburg, Ringwall)', 'Burg (Fliehburg, Ringwall) ist eine ur- oder frühgeschichtliche runde, ovale oder an Gegebenheiten des Geländes (Böschungskanten) angepasste Befestigungsanlage, die aus einem Erdwall mit oder ohne Holzeinbauten besteht.');
INSERT INTO ax_archaeologischertyp_historischesbauwerkoderhistorischee (wert, beschreibung, dokumentation) VALUES (1420, 'Schanze', 'Schanze ist eine mittelalterliche oder neuzeitliche, in der Regel geschlossene, quadratische, rechteckige oder sternförmige Wallanlage mit Außengraben.');
INSERT INTO ax_archaeologischertyp_historischesbauwerkoderhistorischee (wert, beschreibung, dokumentation) VALUES (1430, 'Lager', 'Lager ist die Bezeichnung für ein befestigtes Truppenlager in der Römer- oder in der Neuzeit (z.B. bei Belagerungen im 30 jährigen Krieg).');
INSERT INTO ax_archaeologischertyp_historischesbauwerkoderhistorischee (wert, beschreibung, dokumentation) VALUES (1500, 'Historische Mauer', 'Historische Mauer ist eine Mauer mit kulturgeschichtlicher Bedeutung.');
INSERT INTO ax_archaeologischertyp_historischesbauwerkoderhistorischee (wert, beschreibung, dokumentation) VALUES (1510, 'Stadtmauer', '');
INSERT INTO ax_archaeologischertyp_historischesbauwerkoderhistorischee (wert, beschreibung, dokumentation) VALUES (1520, 'Sonstige historische Mauer', '');
