INSERT INTO ax_art_dammwalldeich (wert, beschreibung, dokumentation) VALUES (1910, 'Hochwasserdeich', 'Hochwasserdeich ist ein Deich an einem Fliessgewässer oder im Küstengebiet, der dem Schutz eines Gebietes vor Hochwasser oder gegen Sturmfluten dient.');
INSERT INTO ax_art_dammwalldeich (wert, beschreibung, dokumentation) VALUES (1920, 'Hauptdeich, Landesschutzdeich', 'Hauptdeich, Landesschutzdeich ist ein Deich der ersten Deichlinie zum Schutz der Küsten- und Inselgebiete gegen Sturmflut.');
INSERT INTO ax_art_dammwalldeich (wert, beschreibung, dokumentation) VALUES (1930, 'Überlaufdeich', 'Überlaufdeich ist ein Deich vor dem Hauptdeich, der in erster Linie dem Schutz landwirtschaftlich genutzter Flächen gegen leichte Sturmtiden dient und der bei höheren Sturmtiden überströmt wird.');
INSERT INTO ax_art_dammwalldeich (wert, beschreibung, dokumentation) VALUES (1940, 'Leitdeich', 'Leitdeich ist ein dammartiges Bauwerk im Watt, um strömendes Wasser in bestimmte Richtungen zu lenken und zum Schutz von Wasserläufen im Watt (Außentiefs) vor Versandung.');
INSERT INTO ax_art_dammwalldeich (wert, beschreibung, dokumentation) VALUES (1950, 'Polderdeich', 'Polderdeich ist ein vor dem Hauptdeich liegender Deich, der landwirtschaftlich nutzbares Land (z. B. Marschland) schützt.');
INSERT INTO ax_art_dammwalldeich (wert, beschreibung, dokumentation) VALUES (1960, 'Schlafdeich', 'Schlafdeich ist ein ehemaliger Hauptdeich, der infolge einer Vorverlegung der Deichlinie zu einem Binnendeich geworden ist und keine unmittelbare Schutzaufgabe mehr zu erfüllen hat.');
INSERT INTO ax_art_dammwalldeich (wert, beschreibung, dokumentation) VALUES (1970, 'Mitteldeich', 'Mitteldeich ist ein Deich der 2. Deichlinie, auch an größeren Flüssen. Er soll Überschwemmungen beim Bruch des Deiches der ersten Deichlinie verhindern.');
INSERT INTO ax_art_dammwalldeich (wert, beschreibung, dokumentation) VALUES (1980, 'Binnendeich', 'Binnendeich ist ein Deich an kleineren Flüssen, der Überschwemmungen durch ablaufendes Oberflächenwasser verhindern soll.');
