INSERT INTO ax_art_gewaessermerkmal (wert, beschreibung, dokumentation) VALUES (1610, 'Quelle', 'Quelle ist eine natürliche, örtlich begrenzte Austrittsstelle von Wasser.');
INSERT INTO ax_art_gewaessermerkmal (wert, beschreibung, dokumentation) VALUES (1620, 'Wasserfall', 'Wasserfall ist ein senkrechter oder nahezu senkrechter Absturz eines Wasserlaufs, der über eine oder mehrere natürliche Stufen verlaufen kann.');
INSERT INTO ax_art_gewaessermerkmal (wert, beschreibung, dokumentation) VALUES (1630, 'Stromschnelle', 'Stromschnelle ist eine Flussstrecke mit höherer Strömungsgeschwindigkeit durch ein besonders starkes Gefälle sowie oft auch geringerer Wassertiefe.');
INSERT INTO ax_art_gewaessermerkmal (wert, beschreibung, dokumentation) VALUES (1640, 'Sandbank', 'Sandbank ist eine vegetationslose Sand- oder Kiesablagerung auf dem Meeresboden oder in Flüssen, die durch Brandung oder Strömung aufgebaut wird.');
INSERT INTO ax_art_gewaessermerkmal (wert, beschreibung, dokumentation) VALUES (1650, 'Watt', 'Watt ist ein aus Sand oder Schlick bestehender Boden an flachen Gezeitenküsten und in Mündungstrichtern von Flüssen, der bei Ebbe ganz oder teilweise trocken fällt und bei Flut vom sogenannten Wattenmeer überspült wird.');
INSERT INTO ax_art_gewaessermerkmal (wert, beschreibung, dokumentation) VALUES (1660, 'Priel', 'Priel ist eine natürliche Rinne im Watt, die auch bei Ebbe Wasser führt.');
