INSERT INTO ax_art_schifffahrtsliniefaehrverkehr (wert, beschreibung, dokumentation) VALUES (1710, 'Autofährverkehr', 'Autofährverkehr ist ein in der Regel nach festem Fahrplan über Flüsse, Seen, Kanäle, Meerengen oder Meeresarme stattfindender Schiffsverkehr zwischen zwei Anlegestellen speziell für Fahrzeuge des Straßenverkehrs.');
