INSERT INTO ax_art_wegpfadsteig (wert, beschreibung, dokumentation) VALUES (1105, 'Karren- und Ziehweg', 'Karrenweg ist ein Weg im Gebirge, der meist sehr steil ist und nur mit einem Gespann befahren werden kann. Ziehweg ist ein Weg, der der Holzabfuhr im Gebirge dient.');
