INSERT INTO ax_artderfestlegung_denkmalschutzrecht (wert, beschreibung, dokumentation) VALUES (2810, 'Archäologisches Denkmal', 'Archäologisches Denkmal ist ein Ort, an dem Reste vor-  oder frühgeschichtlicher Kulturen oder früheren menschlichen Wirkens entdeckt wurden.');
INSERT INTO ax_artderfestlegung_denkmalschutzrecht (wert, beschreibung, dokumentation) VALUES (2820, 'Bodendenkmal', 'Bodendenkmal ist eine mit dem Boden verbundene oder im Boden verborgene Sache, die von Menschen geschaffen oder bearbeitet wurde oder Aufschluss über menschliches Leben in vergangenen Zeiten gibt und deshalb erhaltenswert ist.');
