INSERT INTO ax_artderfestlegung_denkmalschutzrecht (wert, beschreibung, dokumentation) VALUES (3118, 'Lager', 'Lager ist die Bezeichnung für ein befestigtes Truppenlager in der Römer- oder in der Neuzeit (z.B. bei Belagerungen im 30 jährigen Krieg).');
INSERT INTO ax_artderfestlegung_denkmalschutzrecht (wert, beschreibung, dokumentation) VALUES (3120, 'Wachturm (römisch), Warte', 'Wachtturm (römisch), Warte ist ein allein oder in Verbindung mit einem Befestigungssystem (Limes) stehender Beobachtungsturm.');
