INSERT INTO ax_artderfestlegung_denkmalschutzrecht (wert, beschreibung, dokumentation) VALUES (3130, 'Befestigung (Wall, Graben)', 'Befestigung (Wall, Graben) ist ein aus Erde aufgeschütteter Grenz-, Schutz- oder Stadtwall. Zu der Befestigung (Wall) zählen auch Limes und Landwehr.');
INSERT INTO ax_artderfestlegung_denkmalschutzrecht (wert, beschreibung, dokumentation) VALUES (3131, 'Grenzwall, Schutzwall', 'Grenzwall, Schutzwall ist ein aus Erde aufgeschütteter Wall.');
