INSERT INTO ax_artderfestlegung_denkmalschutzrecht (wert, beschreibung, dokumentation) VALUES (3133, 'Landwehr', 'Landwehr ist eine mittelalterliche Verteidigungslinie aus ein- bis mehrfachem Wallgrabensystem.');
