INSERT INTO ax_artderfestlegung_naturumweltoderbodenschutzrecht (wert, beschreibung, dokumentation) VALUES (1610, 'Schutzfläche nach Europarecht', '');
INSERT INTO ax_artderfestlegung_naturumweltoderbodenschutzrecht (wert, beschreibung, dokumentation) VALUES (1611, 'Flora-Fauna-Habitat-Gebiet', 'Flora-Fauna-Habitat-Gebiet ist ein Schutzgebiet von gemeinschaftlicher Bedeutung im Sinne der Richtlinie 92/43/EWG des Rates der Europäischen Wirtschaftsgemeinschaft zur Erhaltung der natürlichen Lebensräume sowie der wildlebenden Pflanzen und Tiere.');
INSERT INTO ax_artderfestlegung_naturumweltoderbodenschutzrecht (wert, beschreibung, dokumentation) VALUES (1612, 'Vogelschutzgebiet', 'Vogelschutzgebiet ist ein besonderes Schutzgebiet (Special Protected Area, SPA) im Sinne Artikel 4 Abs. 1 der Richtlinie 79/409/EWG des Rates der Europäischen Wirtschaftsgemeinschaft über die Erhaltung der wildlebenden Vogelarten (Vogelschutzrichtlinie).');
INSERT INTO ax_artderfestlegung_naturumweltoderbodenschutzrecht (wert, beschreibung, dokumentation) VALUES (1620, 'Schutzflächen nach Landesnaturschutzgesetz', '');
INSERT INTO ax_artderfestlegung_naturumweltoderbodenschutzrecht (wert, beschreibung, dokumentation) VALUES (1621, 'Naturschutzgebiet', 'Naturschutzgebiet ist ein rechtsverbindlich festgesetztes Gebiet, in dem ein besonderer Schutz von Natur und Landschaft in ihrer Ganzheit oder in einzelnen Teilen zur Erhaltung von Lebensgemeinschaften oder Biotopen bestimmter wildlebender Tier- und Pflanzenarten, aus wissenschaftlichen, naturgeschichtlichen oder landeskundlichen Gründen oder wegen ihrer Seltenheit, besonderen Eigenart oder hervorragenden Schönheit erforderlich ist.');
INSERT INTO ax_artderfestlegung_naturumweltoderbodenschutzrecht (wert, beschreibung, dokumentation) VALUES (1622, 'Geschützter Landschaftsbestandteil', 'Geschützter Landschaftsbestandteil ist ein rechtsverbindlich festgesetzter Teil von Natur und Landschaft, dessen besonderer Schutz zur Sicherstellung der Leistungsfähigkeit des Naturhaushalts, zur Belebung, Gliederung oder Pflege des Orts- und Landschaftsbildes oder zur Abwehr schädlicher Einwirkungen erforderlich ist.');
INSERT INTO ax_artderfestlegung_naturumweltoderbodenschutzrecht (wert, beschreibung, dokumentation) VALUES (1623, 'Landschaftsschutzgebiet', 'Landschaftsschutzgebiet ist ein rechtsverbindlich festgesetztes Gebiet, in dem ein besonderer Schutz von Natur und Landschaft zur Erhaltung oder Wiederherstellung der Leistungsfähigkeit des Naturhaushalts oder der Nutzungsfähigkeit der Naturgüter, wegen der Vielfalt, Eigenart oder Schönheit des Landschaftsbildes oder wegen der besonderen Bedeutung für die Erholung erforderlich ist.');
INSERT INTO ax_artderfestlegung_naturumweltoderbodenschutzrecht (wert, beschreibung, dokumentation) VALUES (1624, 'Naturpark', 'Naturpark ist ein einheitlich zu entwickelndes und zu pflegendes Gebiet, das großräumig ist, überwiegend Landschaftsschutzgebiet oder Naturschutzgebiet ist, sich wegen seiner landschaftlichen Voraussetzungen für die Erholung besonders eignet und nach den Grundsätzen und Zielen der Raumordnung und der Landesplanung für die Erholung oder den Fremdenverkehr vorgesehen ist.');
