INSERT INTO ax_bahnhofskategorie_bahnverkehrsanlage (wert, beschreibung, dokumentation) VALUES (1020, 'Haltestelle', 'Haltestelle ist eine Anlage im Netz der Schienenbahnen und der Seilbahnen (Bahnkategorie Schwebebahn) zur Abwicklung des Personen- und Güterverkehrs entsprechend der Angaben des Betreibers.');
