INSERT INTO ax_bahnkategorie_seilbahnschwebebahn (wert, beschreibung, dokumentation) VALUES (2100, 'Luftseilbahn, Großkabinenbahn', 'Luftseilbahn, Großkabinenbahn ist die Bezeichnung für eine Seilbahn, die Personen befördert und Güter transportiert. Die Kabinen und Transporteinrichtungen werden an einem Zugseil über ein Tragseil fortbewegt.');
INSERT INTO ax_bahnkategorie_seilbahnschwebebahn (wert, beschreibung, dokumentation) VALUES (2200, 'Kabinenbahn, Umlaufseilbahn', 'Kabinenbahn, Umlaufseilbahn ist die Bezeichnung für eine Seilbahn zur Beförderung von Personen und zum Transport von Gütern. Die Wagen oder Kabinen sind an einem umlaufenden Seil festgeklemmt.');
INSERT INTO ax_bahnkategorie_seilbahnschwebebahn (wert, beschreibung, dokumentation) VALUES (2300, 'Sessellift', 'Sessellift ist die Bezeichnung für eine Seilbahn zur Beförderung von Personen in Sitzen ohne Kabinenverkleidung.');
INSERT INTO ax_bahnkategorie_seilbahnschwebebahn (wert, beschreibung, dokumentation) VALUES (2400, 'Ski-, Schlepplift', 'Ski-, Schlepplift ist die Bezeichnung für eine Seilbahn, mit der Skifahrer stehend den Berg hinauf gezogen werden.');
INSERT INTO ax_bahnkategorie_seilbahnschwebebahn (wert, beschreibung, dokumentation) VALUES (2500, 'Schwebebahn', 'Schwebebahn ist die Bezeichnung für eine Bahn, bei der elektrisch angetriebene Fahrzeuge unter einer Fahrschiene hängen.');
INSERT INTO ax_bahnkategorie_seilbahnschwebebahn (wert, beschreibung, dokumentation) VALUES (2600, 'Materialseilbahn', 'Materialseilbahn ist die Bezeichnung für eine Seilbahn, die ausschließlich Güter transportiert.');


