INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2010, 'Durchlass', 'Durchlass ist ein Bauwerk, in dem ein Gewässer unter einem auf der Erdoberfläche liegenden Hindernis (Verkehrsweg, Siedlungsfläche) hindurchgeführt wird oder unter der Erdoberfläche in freier Feldlage oder abgedeckt (verdolt) auf der Erdoberfläche verläuft.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2011, 'Rohrdurchlass', 'Rohrdurchlass ist ein Bauwerk zur Unterführung eines Gewässers unter einem Verkehrsweg.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2012, 'Düker', 'Düker ist ein Kreuzungsbauwerk, in dem ein Gewässer unter einem anderen Gewässer, einem Geländeeinschnitt oder einem tieferliegenden Hindernis unter Druck hindurchgeleitet wird.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2020, 'Rückhaltebecken', '');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2030, 'Staumauer', 'Staumauer ist ein aus Mauerwerk oder Beton bestehendes Absperrbauwerk zur Erzeugung eines Staus.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2040, 'Staudamm', 'Staudamm ist ein meist aus natürlichen Baustoffen, meist aufgeschüttetes Absperrbauwerk zur Erzeugung eines Staus.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2050, 'Wehr', 'Wehr ist ein festes oder mit beweglichen Teilen ausgestattetes Bauwerk im Flussbett zur Regulierung des Wasserabflusses.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2060, 'Sicherheitstor', 'Sicherheitstor ist ein Bauwerk zum Abschließen von Kanalstrecken, um bei Schäden das Auslaufen der gesamten Kanalhaltung zu verhindern.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2070, 'Siel', 'Siel ist ein Bauwerk mit Verschlusseinrichtung (gegen rückströmendes Wasser) zum Durchleiten eines oberirdischen Gewässers durch einen Deich.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2080, 'Sperrwerk', 'Sperrwerk ist ein Bauwerk in einem Tideflussgewässer mit Verschlusseinrichtung zum Absperren bestimmter Tiden, vor allem zum Schutz gegen Sturmfluten auch bei Tidehäfen.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2090, 'Schöpfwerk', 'Schöpfwerk ist eine Anlage, in der Pumpen Wasser einem höher gelegenen Vorfluter zuführen, u.a. zur künstlichen Entwässerung von landwirtschaftlich genutzten Flächen.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2110, 'Fischtreppe', '');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2120, 'Pegel', 'Pegel ist eine Messeinrichtung zur Feststellung des Wasserstandes von Gewässern.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2130, 'Uferbefestigung', 'Uferbefestigung ist eine Anlage zum Schutze des Ufers.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2131, 'Wellenbrecher, Buhne', 'Wellenbrecher, Buhne ist ein ins Meer oder in den Fluss hinein angelegtes Bauwerk zum Uferschutz aus Buschwerk, Holz, Stein, Stahlbeton oder Asphalt.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2132, 'Lahnung', 'Lahnung ist ein ins Meer hineingebauter Damm aus Buschwerk, das zwischen Pfahlreihen fest eingepackt und verschnürt ist. Mehrere netzartig angelegte Dämme dienen zur Landgewinnung (z. B. im Watt) indem die Schlickablagerung gefördert wird.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2133, 'Hafendamm, Mole', 'Hafendamm, Mole ist ein in das Wasser vorgestreckter Steindamm, der eine Hafeneinfahrt begrenzt und das Hafenbecken vor Strömung und Wellenschlag schützt.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2134, 'Höft', 'Höft ist eine vorspringende Ecke bei Kaimauern in einem Hafen.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2135, 'Deckwerk', 'Deckwerk ist ein geböschter Uferschutz an Schardeichen (Deiche ohne Vorland).');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (2136, 'Ufermauer, Kaimauer', 'Ufermauer, Kaimauer ist eine Mauer entlang der Uferlinie eines Gewässers zum Schutz des Ufers bzw. eine Uferbefestigung im Hafengelände zum Anlegen von Schiffen.');
INSERT INTO ax_bauwerksfunktion_bauwerkimgewaesserbereich (wert, beschreibung, dokumentation) VALUES (9999, 'Sonstiges', 'Sonstiges bedeutet, dass die Bauwerksfunktion bekannt, aber nicht in der Attributwertliste aufgeführt ist.');


