INSERT INTO ax_bauwerksfunktion_bauwerkimverkehrsbereich (wert, beschreibung, dokumentation) VALUES (1800, 'Brücke', 'Brücke ist ein Bauwerk zum Zweck der Überführung eines Verkehrsweges über einen anderen Verkehrsweg  oder über ein Gewässer sowie zur Überführung über ein tieferliegendes Gelände.');
INSERT INTO ax_bauwerksfunktion_bauwerkimverkehrsbereich (wert, beschreibung, dokumentation) VALUES (1801, 'Mehrstöckige Brücke', 'Mehrstöckige Brücke ist eine Brücke, die mit Verkehrswegen in mehreren Etagen ausgestattet ist.');
INSERT INTO ax_bauwerksfunktion_bauwerkimverkehrsbereich (wert, beschreibung, dokumentation) VALUES (1802, 'Bogenbrücke', 'Bogenbrücke ist eine Brücke, bei der das Tragwerk aus Bögen besteht.');
INSERT INTO ax_bauwerksfunktion_bauwerkimverkehrsbereich (wert, beschreibung, dokumentation) VALUES (1803, 'Fachwerkbrücke', 'Fachwerkbrücke ist eine Brücke, bei der das Tragwerk aus starr zusammengesetzten Tragbalken (Holz oder Metall) besteht.');
INSERT INTO ax_bauwerksfunktion_bauwerkimverkehrsbereich (wert, beschreibung, dokumentation) VALUES (1804, 'Hängebrücke', 'Hängebrücke ist eine Brücke, bei der das Tragwerk von Hängegurten (Kabel) an einem oder mehreren Pylonen gehalten wird.');
INSERT INTO ax_bauwerksfunktion_bauwerkimverkehrsbereich (wert, beschreibung, dokumentation) VALUES (1805, 'Pontonbrücke', 'Pontonbrücke ist eine Behelfsbrücke, die sich aus kastenförmigen Schwimmkörpern zusammensetzt.');
INSERT INTO ax_bauwerksfunktion_bauwerkimverkehrsbereich (wert, beschreibung, dokumentation) VALUES (1806, 'Drehbrücke', 'Drehbrücke ist eine Brücke, bei der sich das Tragwerk um einen senkrechten Zapfen (Königsstuhl) dreht.');
INSERT INTO ax_bauwerksfunktion_bauwerkimverkehrsbereich (wert, beschreibung, dokumentation) VALUES (1807, 'Hebebrücke', 'Hebebrücke ist eine Brücke, bei der das Tragwerk an Seilen oder Ketten emporgehoben wird.');
INSERT INTO ax_bauwerksfunktion_bauwerkimverkehrsbereich (wert, beschreibung, dokumentation) VALUES (1808, 'Zugbrücke', 'Zugbrücke ist eine Brücke, bei der das Tragwerk um eine waagerechte Achse hochgeklappt wird.');
