INSERT INTO ax_bauwerksfunktion_bauwerkimverkehrsbereich (wert, beschreibung, dokumentation) VALUES (1880, 'Schutzgalerie, Einhausung', 'Schutzgalerie, Einhausung ist eine bauliche Einrichtung an Verkehrswegen zum Schutz gegen Lawinen, Schneeverwehungen, Steinschlägen sowie zum Schutz  gegen Emission. 
