INSERT INTO ax_bauwerksfunktion_bauwerkimverkehrsbereich (wert, beschreibung, dokumentation) VALUES (1900, 'Durchfahrt', 'Durchfahrt ist eine Stelle, an der mit Fahrzeugen durch ein Bauwerk (z.B. ein Turm, eine Mauer) hindurch gefahren werden kann.');
