INSERT INTO ax_bauwerksfunktion_sonstigesbauwerkodersonstigeeinrichtun (wert, beschreibung, dokumentation) VALUES (1791, 'Höckerlinie', 'Höckerlinie bezeichnet die ehemalige Panzersperre Westwall.');
