INSERT INTO ax_bauwerksfunktion_transportanlage (wert, beschreibung, dokumentation) VALUES (1102, 'Förderband, Bandstraße', 'Förderband, Bandstraße ist ein mechanisch bewegtes Band zum Transport von Gütern.');
INSERT INTO ax_bauwerksfunktion_transportanlage (wert, beschreibung, dokumentation) VALUES (1103, 'Pumpe', 'Pumpe ist eine Vorrichtung zum An-, Absaugen oder Injizieren von Flüssigkeiten oder Gasen; Verdichtungsstation  für Gase.');


