INSERT INTO ax_bauwerksfunktion_turm (wert, beschreibung, dokumentation) VALUES (1001, 'Wasserturm', 'Wasserturm ist ein hochgelegenes Bauwerk mit einem Behälter, in dem Wasser für die Wasserversorgung und Konstanthaltung des Wasserdruckes gespeichert wird.');
INSERT INTO ax_bauwerksfunktion_turm (wert, beschreibung, dokumentation) VALUES (1002, 'Kirchturm, Glockenturm', 'Kirchturm, Glockenturm ist ein freistehender Turm, der die Glockenstube mit den Glocken aufnimmt.');
INSERT INTO ax_bauwerksfunktion_turm (wert, beschreibung, dokumentation) VALUES (1003, 'Aussichtsturm', 'Aussichtsturm ist ein Bauwerk, das ausschließlich der Fernsicht dient.');
INSERT INTO ax_bauwerksfunktion_turm (wert, beschreibung, dokumentation) VALUES (1004, 'Kontrollturm', 'Kontrollturm (Tower) ist ein Bauwerk auf dem Fluggelände, in dem die für die Lenkung und Überwachung des Flugverkehrs erforderlichen Anlagen und Einrichtungen untergebracht sind.');
INSERT INTO ax_bauwerksfunktion_turm (wert, beschreibung, dokumentation) VALUES (1005, 'Kühlturm', 'Kühlturm ist eine turmartige Kühlanlage (Nass- oder Trockenkühlturm), in der erwärmtes Kühlwasser insbesondere von Kraftwerken rückgekühlt wird.');
INSERT INTO ax_bauwerksfunktion_turm (wert, beschreibung, dokumentation) VALUES (1006, 'Leuchtturm', 'Leuchtturm ist ein als Schifffahrtszeichen dienender hoher Turm, ausgerüstet mit einem starken Leuchtfeuer verschiedener Kennungen an der Turmspitze und mit anderen, der Schifffahrt dienenden Signalen.');
INSERT INTO ax_bauwerksfunktion_turm (wert, beschreibung, dokumentation) VALUES (1007, 'Feuerwachturm', 'Feuerwachturm ist ein Turm, der zum Erkennen von Gefahren (Feuer) dient.');
INSERT INTO ax_bauwerksfunktion_turm (wert, beschreibung, dokumentation) VALUES (1008, 'Sende-, Funkturm, Fernmeldeturm', 'Sende-, Funkturm, Fernmeldeturm ist ein Bauwerk, ausgerüstet mit Sende - und Empfangsantennen zum Übertragen und Empfangen von Nachrichten aller Arten von Telekommunikation.');
INSERT INTO ax_bauwerksfunktion_turm (wert, beschreibung, dokumentation) VALUES (1009, 'Stadt-, Torturm', 'Stadtturm ist ein historischer Turm, der das Stadtbild prägt. Torturm ist der auf einem Tor stehende Turm, wobei das Tor allein stehen oder in eine Befestigungsanlage eingebunden sein kann.');
INSERT INTO ax_bauwerksfunktion_turm (wert, beschreibung, dokumentation) VALUES (1010, 'Förderturm', 'Förderturm ist ein Turm über einem Schacht. An Förderseile, die über Seilscheiben im Turm geführt werden, werden Lasten in den Schacht gesenkt oder aus dem Schacht gehoben.');
INSERT INTO ax_bauwerksfunktion_turm (wert, beschreibung, dokumentation) VALUES (1011, 'Bohrturm', 'Bohrturm ist ein zur Gewinnung von Erdöl und Erdgas verwendetes, meist aus einer Stahlkonstruktion bestehendes Gerüst, in dem das Bohrgestänge aufgehängt ist.');
INSERT INTO ax_bauwerksfunktion_turm (wert, beschreibung, dokumentation) VALUES (1012, 'Schloss-, Burgturm', 'Schloss-, Burgturm ist ein Turm innerhalb einer Schloss- bzw. einer Burganlage, auch Bergfried genannt.');
INSERT INTO ax_bauwerksfunktion_turm (wert, beschreibung, dokumentation) VALUES (9998, 'Nach Quellenlage nicht zu spezifizieren', 'Nach Quellenlage nicht zu spezifizieren bedeutet, dass zum Zeitpunkt der Erhebung keine Funktion zuweisbar war.');
INSERT INTO ax_bauwerksfunktion_turm (wert, beschreibung, dokumentation) VALUES (9999, 'Sonstiges', 'Sonstiges bedeutet, dass die Funktion bekannt, aber nicht in der Attributwertliste aufgeführt ist.');


