INSERT INTO ax_blattart_buchungsblatt (wert, beschreibung, dokumentation) VALUES (1000, 'Grundbuchblatt', 'Ein Grundbuchblatt ist ein Buchungsblatt, das die Buchung im Grundbuch enthält.');
INSERT INTO ax_blattart_buchungsblatt (wert, beschreibung, dokumentation) VALUES (2000, 'Katasterblatt', 'Ein Katasterblatt ist ein Buchungsblatt, das die Buchung im Liegenschaftskataster enthält.');
INSERT INTO ax_blattart_buchungsblatt (wert, beschreibung, dokumentation) VALUES (3000, 'Pseudoblatt', 'Ein Pseudoblatt ist ein Buchungsblatt, das die Buchung, die bereits vor Eintrag im Grundbuch Rechtskraft erlangt hat, enthält (z.B. Übernahme von Flurbereinigungsverfahren, Umlegungsverfahren).');
INSERT INTO ax_blattart_buchungsblatt (wert, beschreibung, dokumentation) VALUES (4000, 'Erwerberblatt', 'Ein Erwerberblatt ist ein Buchungsblatt, das die Buchung, die bereits im Liegenschaftskataster, aber noch nicht im Grundbuch gebucht ist, enthält (Buchungsvorschlag für die Grundbuchverwaltung).
Pseudoblatt und Erwerberblatt werden nach Eintragung in das Grundbuch historisch.');
INSERT INTO ax_blattart_buchungsblatt (wert, beschreibung, dokumentation) VALUES (5000, 'Fiktives Blatt', 'Das fiktive Blatt enthält die aufgeteilten Grundstücke und Rechte als Ganzes. Es bildet um die Miteigentumsanteile eine fachliche Klammer.');


