INSERT INTO ax_funktion_fliessgewaesser (wert, beschreibung, dokumentation) VALUES (8200, 'Fluss', 'Fluss ist ein natürliches, fließendes Gewässer (ggf. auch mit begradigten, kanalisierten Teilstücken), das wegen seiner Größe und Bedeutung nach allgemeiner Verkehrsauffassung als Fluss angesprochen wird.');
INSERT INTO ax_funktion_fliessgewaesser (wert, beschreibung, dokumentation) VALUES (8210, 'Altwasser', 'Altwasser ist ein Teil eines Fließgewässers, der bei einer Begradigung vom fließenden Gewässer abgeschnitten wurde und wichtiger Lebensraum für z. B. Wasser-vögel, Amphibien und Libellen ist.');
INSERT INTO ax_funktion_fliessgewaesser (wert, beschreibung, dokumentation) VALUES (8220, 'Altarm', '');
INSERT INTO ax_funktion_fliessgewaesser (wert, beschreibung, dokumentation) VALUES (8230, 'Flussmündungstrichter', 'Flussmündungstrichter ist der Bereich des Flusses im Übergang zum Meer. Er beginnt dort, wo die bis dahin etwa parallel verlaufenden Ufer des Flusses sich trichterförmig zur offenen See hin erweitern. Die Abgrenzungen der Flussmündungstrichter ergeben sich aus dem Bundeswasserstraßengesetz (meerseitig) und den Bekanntmachungen der Wasser- und Schifffahrtsverwaltung sowie höchst-richterlicher Rechtsprechung (binnenseitig).');
INSERT INTO ax_funktion_fliessgewaesser (wert, beschreibung, dokumentation) VALUES (8300, 'Kanal', 'Kanal ist ein für die Schifffahrt angelegter, künstlicher Wasserlauf.');
INSERT INTO ax_funktion_fliessgewaesser (wert, beschreibung, dokumentation) VALUES (8400, 'Graben', 'Graben ist ein ständig oder zeitweise fließendes, künstlich angelegtes oder natürliches Gewässer, das nach allgemeiner Verkehrsauffassung als Graben anzusehen ist.');
INSERT INTO ax_funktion_fliessgewaesser (wert, beschreibung, dokumentation) VALUES (8410, 'Fleet', '');
INSERT INTO ax_funktion_fliessgewaesser (wert, beschreibung, dokumentation) VALUES (8500, 'Bach', 'Bach ist ein natürliches, fließendes Gewässer, das wegen seiner geringen Größe und Bedeutung nach allgemeiner Verkehrsauffassung als Bach anzusehen ist.');


