INSERT INTO ax_funktion_schutzgebietnachwasserrecht (wert, beschreibung, dokumentation) VALUES (2111, 'Öffentliche Wasserversorgung', 'Öffentliche Wasserversorgung ist die Aufgabe eines Wasserschutzgebietes. Ziel ist es, die Gewässer der öffentlichen Wasserversorgung vor nachteiligen Einwirkungen zu schützen.');
