INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (1311, 'Ferienhaus', '');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (1312, 'Wochenendhaus', 'Wochenendhaus ist ein Gebäude, in dem dauerhaftes Wohnen möglich, aber nicht gestattet ist. Es dient nur zum zeitlich begrenzten Aufenthalt in der Freizeit, beispielsweise am Wochenende oder im Urlaub und steht i.d.R. in einem besonders dafür ausgewiesenen Gebiet (Wochenendhausgebiet).');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (1313, 'Gartenhaus', 'Gartenhaus ist ein eingeschossiges Gebäude in einfacher Ausführung, z.B.ohne Feuerstätte und ohne Toilette. Es dient hauptsächlich dem Unterbringen von Gartengeräten oder dem Überwintern von Pflanzen. Stellt es bei der Nutzung des Gartens eine Ruhe oder Erholungsstätte dar, ist es nur zum vorübergehenden Aufenthalt gedacht, nicht jedoch zum Wohnen oder Übernachten.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (2000, 'Gebäude für Wirtschaft oder Gewerbe', 'Gebäude für Wirtschaft oder Gewerbe ist ein Gebäude, das der Produktion von Waren, der Verteilung von Gütern und dem Angebot von Dienstleistungen dient.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (2010, 'Gebäude für Handel und Dienstleistungen', 'Gebäude für Handel und Dienstleistungen ist ein Gebäude, in dem Arbeitsleistungen, die nicht der Produktion von materiellen Gütern dienen, angeboten werden. Dazu gehört u.a. der Handel (Ankauf, Transport, Verkauf) mit Gütern, Kapital oder Wissen.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (2020, 'Bürogebäude', 'Bürogebäude ist ein Gebäude, in dem private Wirtschaftunternehmen ihre Verwaltungsarbeit durchführen.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (2030, 'Kreditinstitut', 'Kreditinstitut ist ein Gebäude, in dem Unternehmen gewerbsmäßig Geldgeschäfte (Verwaltung von Ersparnissen, Vergabe von Krediten) betreiben, die einen kaufmännisch eingerichteten Geschäftsbetrieb erfordern.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (2040, 'Versicherung', 'Versicherung ist ein Gebäude, in dem Versicherungsunternehmen gewerbsmäßige Versicherungsgeschäfte betreiben.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (2050, 'Geschäftsgebäude', 'Geschäftsgebäude ist ein Gebäude, in dem Ein- und Verkauf von Waren stattfindet.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (2051, 'Kaufhaus', 'Kaufhaus ist ein Gebäude, meist mit mehreren Stockwerken, in dem breite Warensortimente zum Kauf angeboten werden.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (2052, 'Einkaufszentrum', 'Einkaufszentrum ist ein Gebäude oder Gebäudekomplex, in dem mehrere Geschäfte untergebracht sind.');
