INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (2056, 'Apotheke', 'Apotheke ist ein Geschäft, in dem Arzneimittel hergestellt und verkauft werden.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (2060, 'Messehalle', 'Messehalle ist ein Gebäude, das zur Ausstellung von Kunstgegenständen oder Wirtschaftsgütern dient.');
