INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (2071, 'Hotel, Motel, Pension', 'Hotel, Motel, Pension ist ein Gebäude mit Beherbergungs- und/oder Verpflegungsbetrieb nach Service, Ausstattung und Qualität in verschiedene Kategorien eingeteilt. Das Motel ist besonders eingerichtet für Reisende mit Kraftfahrzeug an verkehrsreichen Straßen.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (2072, 'Jugendherberge', 'Jugendherberge ist eine zur Förderung von Jugendreisen dienende Aufenthalts- und Übernachtungsstätte.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (2073, 'Hütte (mit Übernachtungsmöglichkeit)', 'Hütte (mit Übernachtungsmöglichkeit) ist ein Gebäude außerhalb von Ortschaften, meist in den Bergen, in dem Menschen übernachten und Schutz suchen können.');
