INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3013, 'Post', 'Post ist ein Gebäude, in dem die Post Dienstleistungen anbietet.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3014, 'Zollamt', 'Zollamt ist ein Gebäude für die Zollabfertigung an der Staatsgrenze (Grenzzollamt) oder im Inland (Binnenzollamt).');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3015, 'Gericht', 'Gericht ist ein Gebäude, in dem Rechtsprechung und Rechtspflege stattfinden.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3016, 'Botschaft, Konsulat', 'Botschaft, Konsulat ist ein Gebäude, in dem eine ständige diplomatische Vertretung ersten Rangs eines fremden Staates oder einer  internationalen Organisation untergebracht ist.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3017, 'Kreisverwaltung', '');
