INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3020, 'Gebäude für Bildung und Forschung', 'Gebäude für Bildung und Forschung ist ein Gebäude, in dem durch Ausbildung Wissen und Können auf verschiedenen Gebieten vermittelt werden bzw. wo neues Wissen durch wissenschaftliche Tätigkeit gewonnen wird.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3021, 'Allgemein bildende Schule', 'Allgemein bildende Schule ist ein Gebäude, in dem Kindern, Jugendlichen und Erwachsenen durch planmäßigen Unterricht Wissen vermittelt wird.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3022, 'Berufsbildende Schule', '');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3023, 'Hochschulgebäude (Fachhochschule, Universität)', 'Hochschulgebäude (Fachhochschule, Universität) ist ein Gebäude, in dem Wissenschaften gelehrt und Forschung betrieben wird.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3024, 'Forschungsinstitut', 'Forschungsinstitut ist ein Gebäude, in dem Forschung betrieben wird.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3030, 'Gebäude für kulturelle Zwecke', 'Gebäude für kulturelle Zwecke ist ein Gebäude, in dem kulturelle Ereignisse stattfinden sowie ein Gebäude von kulturhistorischer Bedeutung.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3031, 'Schloss', 'Schloss ist ein Gebäude, das als repräsentativer Wohnsitz vor allem des Adels dient oder diente.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3032, 'Theater, Oper', 'Theater, Oper ist ein Gebäude, in dem Bühnenstücke aufgeführt werden.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3033, 'Konzertgebäude', 'Konzertgebäude ist ein Gebäude, in dem Musikaufführungen stattfinden.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3034, 'Museum', 'Museum ist ein Gebäude, in dem Sammlungen von (historischen) Objekten oder Reproduktionen davon ausgestellt werden.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3035, 'Rundfunk, Fernsehen', 'Rundfunk-, Fernsehen ist ein Gebäude, in dem Radio- und Fernsehprogramme produziert und gesendet werden.');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3036, 'Veranstaltungsgebäude', 'Veranstaltungsgebäude ist ein Gebäude, das hauptsächlich für kulturelle Zwecke wie z.B. Aufführungen, Ausstellungen, Konzerte genutzt wird');
INSERT INTO ax_gebaeudefunktion (wert, beschreibung, dokumentation) VALUES (3037, 'Bibliothek, Bücherei', 'Bibliothek, Bücherei ist ein Gebäude, in dem Bücher und Zeitschriften gesammelt, aufbewahrt und ausgeliehen werden.');
