INSERT INTO ax_landschaftstyp (wert, beschreibung, dokumentation) VALUES (1100, 'Gebirge', 'Gebirge bezeichnet eine zusammenhängende größere Erhebung der Erdoberfläche. Es besteht aus einzelnen Bergen und Hochflächen, die durch Täler und Senken gegliedert sind.');
INSERT INTO ax_landschaftstyp (wert, beschreibung, dokumentation) VALUES (1200, 'Berg', 'Berg bezeichnet eine über die Umgebung deutlich herausragende Geländeerhebung, einzeln oder als Teil eines Gebirges.');
INSERT INTO ax_landschaftstyp (wert, beschreibung, dokumentation) VALUES (1300, 'Niederung/Senke', 'Niederung/Senke bezeichnet ein tief liegendes Land an Flussläufen und Küsten.');
INSERT INTO ax_landschaftstyp (wert, beschreibung, dokumentation) VALUES (1400, 'Tal', 'Tal bezeichnet eine langgestreckte, offene Hohlform der Erdoberfläche.');
INSERT INTO ax_landschaftstyp (wert, beschreibung, dokumentation) VALUES (1500, 'Tiefebene', 'Tiefebene bezeichnet einen Teil der Erdoberfläche mit fehlenden oder kaum wahrnehmbaren Höhenunterschieden in einer Höhenlage bis etwa 200 m über NN.');
INSERT INTO ax_landschaftstyp (wert, beschreibung, dokumentation) VALUES (1600, 'Plateau/Hochebene', 'Plateau/Hochebene, bezeichnet einen Teil der Erdoberfläche mit fehlenden oder kaum wahrnehmbaren Höhenunterschieden in einer Höhenlage ab etwa 200 m über NN.');
INSERT INTO ax_landschaftstyp (wert, beschreibung, dokumentation) VALUES (1700, 'Mündungsgebiet', 'Mündungsgebiet bezeichnet die typische, durch Ablagerung von Schwebestoffen entstandene Landschaft im Bereich der Mündung eines fließenden Gewässers in ein anderes Binnengewässer oder in ein Meer.');
INSERT INTO ax_landschaftstyp (wert, beschreibung, dokumentation) VALUES (1800, 'Dünenlandschaft', 'Dünenlandschaft ist eine, vom Wind gebildete, durch Sandanhäufungen geprägte Landschaft.');
INSERT INTO ax_landschaftstyp (wert, beschreibung, dokumentation) VALUES (1900, 'Waldlandschaft', 'Waldlandschaft ist eine größere und zusammenhängende Landschaft, bestehend aus mit Forstpflanzen bestockten Flächen (Wald) sowie mit dem Wald verbundene und ihm dienende Flächen.');
INSERT INTO ax_landschaftstyp (wert, beschreibung, dokumentation) VALUES (2000, 'Inselgruppe', 'Inselgruppe ist eine Gruppe mehrerer nahe beieinander liegender Inseln geologisch gleicher Entstehung.');
INSERT INTO ax_landschaftstyp (wert, beschreibung, dokumentation) VALUES (2100, 'Seenlandschaft', 'Seenlandschaft ist eine durch zahlreiche, nahe beieinander liegende Binnenseen geprägte Landschaft.');
INSERT INTO ax_landschaftstyp (wert, beschreibung, dokumentation) VALUES (2200, 'Siedlungslandschaft', 'Siedlungslandschaft ist eine durch Siedlungen geprägte Landschaft.');
INSERT INTO ax_landschaftstyp (wert, beschreibung, dokumentation) VALUES (2300, 'Moorlandschaft', 'Moorlandschaft ist eine durch Moore geprägte Landschaft.');
INSERT INTO ax_landschaftstyp (wert, beschreibung, dokumentation) VALUES (2400, 'Heidelandschaft', 'Heidelandschaft ist eine waldfreie Landschaft der unteren Höhenstufen, die von einer mehr oder weniger lockeren Zwergstrauchformation geprägt wird.');
INSERT INTO ax_landschaftstyp (wert, beschreibung, dokumentation) VALUES (2500, 'Wattlandschaft', 'Wattlandschaft ist ein ebener, im Wirkungsbereich der Gezeiten liegender Küstenstreifen, der bei Hochwasser überflutet wird, bei Niedrigwasser jedoch trocken fällt, wobei das Wasser durch oft tiefe Furchen (Priele) abfließt.');


