INSERT INTO ax_verkehrsbedeutungueberoertlich (wert, beschreibung, dokumentation) VALUES (1001, 'Fernverkehr', 'Fernverkehr findet hauptsächlich auf dem zusammenhängenden Verkehrsnetz der Bundesautobahnen und Bundesstraßen statt.');
INSERT INTO ax_verkehrsbedeutungueberoertlich (wert, beschreibung, dokumentation) VALUES (1002, 'Regionalverkehr', 'Regionalverkehr findet hauptsächlich auf Landes- und Kreisstraßen statt.');
