INSERT INTO ax_vertrauenswuerdigkeit_schwere (wert, beschreibung, dokumentation) VALUES (1100, 'Vertrauenswürdigkeitsstufe Ausgleichung', 'Die Identität der Anschlusspunkte ist überprüft. Die Zuverlässigkeitskriterien sind durch Ausgleichung und durch mathematisch-statistiche Testverfahren festgestellt.');
INSERT INTO ax_vertrauenswuerdigkeit_schwere (wert, beschreibung, dokumentation) VALUES (1300, 'Vertrauenswürdigkeitsstufe ohne Ausgleichung kontrolliert', 'Die Zuverlässigkeitskriterien sind auf andere Weise (z.B. durch Vergleich von Schwereanomalien) festgestellt.');
INSERT INTO ax_vertrauenswuerdigkeit_schwere (wert, beschreibung, dokumentation) VALUES (1400, 'Vertrauenswürdigkeitsstufe unkontrolliert', 'Der Schwerewert ist nicht unabhängig überprüft.');


